"use strict";
// string
const str = "Rimon";
console.log(str);
// number
const age = 22;
console.log(age);
// boolean
const isMarried = false;
console.log(isMarried);
// undefined
const x = undefined;
// null
const y = null;
// any
let z = "Rimon";
console.log(z);
z = 22;
console.log(z);
z = false;
console.log(z);
// unknown
let a = "Rimon";
console.log(a);
a = 22;
console.log(a);
a = false;
console.log(a);
// array
const arr = [1, 2, 3, 4];
console.log(arr);
// tuple
const tuple = ["Rimon", 22];
console.log(tuple);
